CREATE TABLE CUSTOMER (ID BIGINT AUTO_INCREMENT NOT NULL, CREATIONDATE DATE, DATEOFBIRTH DATE, GENDER VARCHAR(255), LASTNAME VARCHAR(255), MOBILEPHONE VARCHAR(9), NAME VARCHAR(255), PASSWORD VARCHAR(32), SURNAME VARCHAR(255), USERNAME VARCHAR(255), CITY VARCHAR(255), PROVINCE VARCHAR(255), STREET VARCHAR(255), ZIPCODE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE GROUPS (ID BIGINT NOT NULL, DESCRIPTION VARCHAR(255), NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE group_customer (user_fk BIGINT NOT NULL, group_fk BIGINT NOT NULL, PRIMARY KEY (user_fk, group_fk))
ALTER TABLE group_customer ADD CONSTRAINT FK_group_customer_user_fk FOREIGN KEY (user_fk) REFERENCES CUSTOMER (ID)
ALTER TABLE group_customer ADD CONSTRAINT FK_group_customer_group_fk FOREIGN KEY (group_fk) REFERENCES GROUPS (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
INSERT INTO SEQUENCE(SEQ_NAME, SEQ_COUNT) values ('SEQ_GEN', 0)
